import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { TaskService } from '../../services/task.service';
import { Task, TaskStatus } from '../../models/task.model';

@Component({
  selector: 'app-task-detail',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="row justify-content-center">
      <div class="col-md-8 col-lg-6">
        <div class="task-form">
          <div class="d-flex justify-content-between align-items-center mb-4">
            <h2><i class="fas fa-eye me-2"></i>Task Details</h2>
            <div>
              <a [routerLink]="['/tasks', task?.id, 'edit']" class="btn btn-warning me-2">
                <i class="fas fa-edit me-1"></i>Edit
              </a>
              <a routerLink="/tasks" class="btn btn-outline-secondary">
                <i class="fas fa-arrow-left me-1"></i>Back to Tasks
              </a>
            </div>
          </div>

          <div *ngIf="loading" class="loading-spinner">
            <div class="spinner-border text-primary" role="status">
              <span class="visually-hidden">Loading...</span>
            </div>
          </div>

          <div *ngIf="error" class="alert alert-danger" role="alert">
            <i class="fas fa-exclamation-triangle me-2"></i>
            {{ error }}
          </div>

          <div *ngIf="!loading && !error && task" class="task-details">
            <div class="row">
              <div class="col-12">
                <div class="mb-4">
                  <h3 class="mb-2">{{ task.title }}</h3>
                  <span class="badge status-badge" [ngClass]="getStatusClass(task.status)">
                    {{ task.status }}
                  </span>
                </div>

                <div *ngIf="task.description" class="mb-4">
                  <h5>Description</h5>
                  <p class="text-muted">{{ task.description }}</p>
                </div>

                <div class="row mb-4">
                  <div class="col-md-6">
                    <h5>Due Date</h5>
                    <p *ngIf="task.dueDate" class="text-muted">
                      <i class="fas fa-calendar me-2"></i>
                      {{ task.dueDate | date:'fullDate' }}
                    </p>
                    <p *ngIf="!task.dueDate" class="text-muted">
                      <i class="fas fa-calendar me-2"></i>
                      No due date set
                    </p>
                  </div>
                  <div class="col-md-6">
                    <h5>Created</h5>
                    <p class="text-muted">
                      <i class="fas fa-clock me-2"></i>
                      {{ task.createdAt | date:'medium' }}
                    </p>
                  </div>
                </div>

                <div *ngIf="task.updatedAt && task.updatedAt !== task.createdAt" class="mb-4">
                  <h5>Last Updated</h5>
                  <p class="text-muted">
                    <i class="fas fa-edit me-2"></i>
                    {{ task.updatedAt | date:'medium' }}
                  </p>
                </div>

                <div class="d-flex justify-content-between">
                  <button (click)="deleteTask()" class="btn btn-danger">
                    <i class="fas fa-trash me-1"></i>Delete Task
                  </button>
                  <div>
                    <a [routerLink]="['/tasks', task.id, 'edit']" class="btn btn-warning me-2">
                      <i class="fas fa-edit me-1"></i>Edit Task
                    </a>
                    <a routerLink="/tasks" class="btn btn-primary">
                      <i class="fas fa-list me-1"></i>All Tasks
                    </a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: []
})
export class TaskDetailComponent implements OnInit {
  task: Task | null = null;
  loading = false;
  error = '';

  constructor(
    private taskService: TaskService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadTask();
  }

  loadTask(): void {
    this.loading = true;
    this.error = '';

    const taskId = Number(this.route.snapshot.paramMap.get('id'));
    
    this.taskService.getTaskById(taskId).subscribe({
      next: (task) => {
        this.task = task;
        this.loading = false;
      },
      error: (error) => {
        this.error = 'Failed to load task. Please try again.';
        this.loading = false;
        console.error('Error loading task:', error);
      }
    });
  }

  deleteTask(): void {
    if (this.task && confirm('Are you sure you want to delete this task?')) {
      this.taskService.deleteTask(this.task.id!).subscribe({
        next: () => {
          this.router.navigate(['/tasks']);
        },
        error: (error) => {
          this.error = 'Failed to delete task. Please try again.';
          console.error('Error deleting task:', error);
        }
      });
    }
  }

  getStatusClass(status: TaskStatus): string {
    switch (status) {
      case TaskStatus.TODO:
        return 'status-todo';
      case TaskStatus.IN_PROGRESS:
        return 'status-in-progress';
      case TaskStatus.DONE:
        return 'status-done';
      default:
        return '';
    }
  }
}
