import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { TaskService } from '../../services/task.service';
import { Task, TaskStatus } from '../../models/task.model';

@Component({
  selector: 'app-task-list',
  standalone: true,
  imports: [CommonModule, RouterLink],
  template: `
    <div class="row">
      <div class="col-12">
        <div class="d-flex justify-content-between align-items-center mb-4">
          <h2><i class="fas fa-list me-2"></i>All Tasks</h2>
          <a routerLink="/tasks/create" class="btn btn-primary">
            <i class="fas fa-plus me-1"></i>Create New Task
          </a>
        </div>

        <div *ngIf="loading" class="loading-spinner">
          <div class="spinner-border text-primary" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
        </div>

        <div *ngIf="error" class="alert alert-danger" role="alert">
          <i class="fas fa-exclamation-triangle me-2"></i>
          {{ error }}
        </div>

        <div *ngIf="!loading && !error && tasks.length === 0" class="empty-state">
          <i class="fas fa-clipboard-list"></i>
          <h4>No tasks found</h4>
          <p>Get started by creating your first task!</p>
          <a routerLink="/tasks/create" class="btn btn-primary">
            <i class="fas fa-plus me-1"></i>Create Task
          </a>
        </div>

        <div *ngIf="!loading && !error && tasks.length > 0" class="row">
          <div *ngFor="let task of tasks" class="col-md-6 col-lg-4 mb-4">
            <div class="card task-card h-100">
              <div class="card-body">
                <div class="d-flex justify-content-between align-items-start mb-2">
                  <h5 class="card-title mb-0">{{ task.title }}</h5>
                  <span class="badge status-badge" [ngClass]="getStatusClass(task.status)">
                    {{ task.status }}
                  </span>
                </div>
                
                <p *ngIf="task.description" class="card-text text-muted">
                  {{ task.description.length > 100 ? (task.description | slice:0:100) + '...' : task.description }}
                </p>
                
                <div *ngIf="task.dueDate" class="mb-3">
                  <small class="text-muted">
                    <i class="fas fa-calendar me-1"></i>
                    Due: {{ task.dueDate | date:'mediumDate' }}
                  </small>
                </div>
                
                <div class="d-flex justify-content-between align-items-center">
                  <small class="text-muted">
                    <i class="fas fa-clock me-1"></i>
                    {{ task.createdAt | date:'short' }}
                  </small>
                  <div class="btn-group" role="group">
                    <a [routerLink]="['/tasks', task.id]" class="btn btn-sm btn-outline-primary btn-action">
                      <i class="fas fa-eye"></i>
                    </a>
                    <a [routerLink]="['/tasks', task.id, 'edit']" class="btn btn-sm btn-outline-warning btn-action">
                      <i class="fas fa-edit"></i>
                    </a>
                    <button (click)="deleteTask(task.id!)" class="btn btn-sm btn-outline-danger btn-action">
                      <i class="fas fa-trash"></i>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: []
})
export class TaskListComponent implements OnInit {
  tasks: Task[] = [];
  loading = false;
  error = '';

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
    this.loadTasks();
  }

  loadTasks(): void {
    this.loading = true;
    this.error = '';
    
    this.taskService.getAllTasks().subscribe({
      next: (tasks) => {
        this.tasks = tasks;
        this.loading = false;
      },
      error: (error) => {
        this.error = 'Failed to load tasks. Please try again.';
        this.loading = false;
        console.error('Error loading tasks:', error);
      }
    });
  }

  deleteTask(id: number): void {
    if (confirm('Are you sure you want to delete this task?')) {
      this.taskService.deleteTask(id).subscribe({
        next: () => {
          this.tasks = this.tasks.filter(task => task.id !== id);
        },
        error: (error) => {
          this.error = 'Failed to delete task. Please try again.';
          console.error('Error deleting task:', error);
        }
      });
    }
  }

  getStatusClass(status: TaskStatus): string {
    switch (status) {
      case TaskStatus.TODO:
        return 'status-todo';
      case TaskStatus.IN_PROGRESS:
        return 'status-in-progress';
      case TaskStatus.DONE:
        return 'status-done';
      default:
        return '';
    }
  }
}
